import React, { useState, useEffect } from 'react'
import { getOrders } from '../dataService'


export default function useOrders() {
    const [orders, setOrders] = useState([])
    let result
    useEffect(() => {
        async function fetchOrders() {
            result = await getOrders()
        }
        fetchOrders()
    }, [])
  return (
    [result]
  )
}
